document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".dropdown-toggle");e.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),e.stopPropagation();const n=this.closest(".dropdown"),t=n.querySelector(".dropdown-menu");document.querySelectorAll(".dropdown-menu.show").forEach(e=>{e!==t&&e.classList.remove("show")}),t.classList.toggle("show")})}),document.addEventListener("click",function(e){e.target.closest(".dropdown")||document.querySelectorAll(".dropdown-menu.show").forEach(e=>{e.classList.remove("show")})}),document.addEventListener("keydown",function(e){e.key==="Escape"&&document.querySelectorAll(".dropdown-menu.show").forEach(e=>{e.classList.remove("show")})}),document.querySelectorAll(".dropdown-item").forEach(e=>{e.addEventListener("click",function(e){const t=this.getAttribute("href");if(t&&t.startsWith("#")){e.preventDefault();const s=this.closest(".dropdown"),o=s.querySelector(".dropdown-menu");o.classList.remove("show");const n=document.querySelector(t);n&&n.scrollIntoView({behavior:"smooth",block:"start"})}})})})